    private void tabela_usuarioMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabela_usuarioMouseClicked
        // TODO add your handling code here:

        int row = tabela_usuario.getSelectedRow();
        DefaultTableModel tbl = (DefaultTableModel) tabela_usuario.getModel();
        campo_id.setText(tbl.getValueAt(row, 0).toString());
        campo_nome.setText(tbl.getValueAt(row, 1).toString());
        campo_sobrenome.setText(tbl.getValueAt(row, 2).toString());
        campo_bairro.setText(tbl.getValueAt(row, 3).toString());
        campo_cidade.setText(tbl.getValueAt(row, 4).toString());
        campo_telefone.setText(tbl.getValueAt(row, 5).toString());


    }//GEN-LAST:event_tabela_usuarioMouseClicked

