    Usuario usuario = new Usuario();
    UsuarioCrud usuarioCrud = new UsuarioCrud();

    public void atualizarTabela() {
        
        Usuario usuario = new Usuario();
        UsuarioCrud usuarioCrud = new UsuarioCrud();
        
        List<Usuario> dados = (List<Usuario>) usuarioCrud.listarUsuarios(usuario);
        DefaultTableModel tbl = (DefaultTableModel) tabela_usuario.getModel();
        tbl.setNumRows(0); //limpar a tabela
        if (dados != null) {
            for (int i = 0; i < dados.size(); i++) {
                tbl.insertRow(tbl.getRowCount(), new Object[]{
                    String.valueOf(dados.get(i).getId()),
                    dados.get(i).getNome(),
                    dados.get(i).getSobrenome(),
                    dados.get(i).getTelefone(),
                    dados.get(i).getBairro(),
                    dados.get(i).getCidade(),});

            }
        } else {
            tbl.insertRow(tbl.getColumnCount(), new Object[]{
                "Não", " há dados ", "cadastrados"
            //null, null, null, null, null
            });

        }
        
    }


